import React, {useState, useEffect} from "react";
import axios from "axios";
import {useSelector, useDispatch} from 'react-redux';
import { isLength, isMatch } from "../../utils/validation/Validation";
import { showErrMsg, showSuccessMsg } from "../../utils/notification/Notification";
import './Profile.css';
const initialState = {
    name: '',
    password: '',
    cf_password: '',
    err: '',
    success: ''
}


function Profile() {
    const auth = useSelector(state => state.auth)
    const token = useSelector(state => state.token)


    const {user, isAdmin} = auth
    const [data, setData] = useState(initialState);
    const {name, password, cf_password, err, success} = data
    const [avatar, setAvatar] = useState(false);
    const [loading, setLoading] = useState(false);
    const [callback, setCallBack] = useState(false);

    return (
        <div className="profile_page">
            <div className="col-left">
                <h2>{isAdmin ? "Admin Profile": "Perfil de usuario"}</h2>

                <div className="avatar">
                    <img src={avatar ? avatar: user.user.avatar} alt="" />
                    <span>
                        <i className="fas fa-camera"></i>
                        <p>Cambiar</p>
                        <input type="file" name="file" id="file_up" />
                    </span>

                </div>

                <div className="form-group">
                    <label htmlFor="name">Nombre</label>
                    <input type="text" name="name" id="name" defaultValue={user.name}
                    placeholder="Su nombre" value={name}></input>
                </div>

                <div className="form-group">
                    <label htmlFor="email">Nombre</label>
                    <input type="email" name="email" id="email" defaultValue={user.email}
                    placeholder="Su email" ></input>
                </div>


                <div className="form-group">
                    <label htmlFor="password">Nueva Contraseña</label>
                    <input type="password" name="password" id="password" 
                    placeholder="Su contrasena" value={password}></input>
                </div>

                <div className="form-group">
                    <label htmlFor="cf_password">Confirmar Contraseña</label>
                    <input type="password" name="cf_password" id="cf_password" 
                    placeholder="Su nueva contrasena" value={cf_password}></input>
                </div>

            </div>

            <div className="col-right"></div>
        </div>
    )
}

export default Profile
