import React from "react";
import './Header.css';
import {Link} from 'react-router-dom';
import {useSelector} from 'react-redux';
import axios from 'axios';

function Header() {
    const auth = useSelector(state => state.auth)


    const {user, isLogged} = auth

    const handleLogout = async () => {
        try{
            await axios.get('/user/logout')
            localStorage.removeItem('firstLogin')
            window.location.href = "/";
        } catch (err) {
            window.location.href = "/"
        }
    }
    const userLink = () => {
        return <li className="drop-nav">
            <Link to="/" className="avatar">
            <img src={user.avatar} alt="/"/> {user.user.name} <i className="fas fa-chevron-down"></i>


            </Link>
            <ul className="dropdown">
                <li> <Link to="/profile">Perfil</Link> </li>
                <li> <Link to="/" onClick={handleLogout}>Cerrar Sesion</Link> </li>
            </ul>
        </li>
    }

    const transForm = {
        transform: isLogged ? "translateY(-5px)" : 0
    }


    return (

        <header>
            <div className="logo">
                <h1><Link to="/">Prueba de Aptitud Academica</Link></h1>
            </div>

            <ul style={transForm}>


                { 
                    isLogged ? userLink() : <li><Link to="/login"><i className="fas fa-user-graduate"></i>Iniciar Sesion</Link></li>

                }
            </ul>
        </header>

    )
}

export default Header;
